"use client"

import NextImage from "next/image"
import { blurhashToBase64 } from "blurhash-base64"

import { type IProps } from "./types"

import { ImageStatic } from "../ImageStatic"

const blur =
  "data:image/svg+xml;base64,"

const altName = {
  avatar: "/svg/profile-null.svg",
  "offer-image": blur,
}

type TTypes = typeof NextImage.defaultProps & IProps

export const NextImageMotion = (props: TTypes) => {
  const { src, ref, alt, hash, className, height, width, ...rest } = props ?? {}

  const blur64 = blurhashToBase64(hash ?? "")

  return typeof src === "string" && src?.includes("http") ? (
    <NextImage
      placeholder={altName.hasOwnProperty(alt) || !!blur64 ? "blur" : "empty"}
      blurDataURL={!!blur64 ? blur64 : blur}
      ref={ref}
      data-image={alt}
      className={className || ""}
      loading="lazy"
      src={src}
      alt={alt}
      height={height}
      width={width}
      style={{
        objectFit: "cover",
      }}
      {...rest}
    />
  ) : (
    <ImageStatic
      src={alt === "avatar" ? "/svg/profile-null.svg" : blur}
      placeholder="blur"
      blurDataURL={blur}
      alt={alt}
      unoptimized
      className={className}
      height={height}
      width={width}
      style={{
        objectFit: "cover",
      }}
      {...rest}
    />
  )
}
